mapper.select:
select dd.*,cl.cph,ddzt.mc ddztMc,wzlx.mc wzlxMc,wz.mc wzMc,yss.mc yssMc,fhdw.mc fhdwMc,shbm.mc shbmMc from ding_dan dd left join ding_dan_zhuang_tai ddzt on dd.ddztId=ddzt.id left join che_liang cl on dd.clId=cl.id left join wu_zi_lei_xing wzlx on cl.wzlxId=wzlx.id left join wu_zi wz on cl.wzId=wz.id left join yun_shu_shang yss on cl.yssId=yss.id left join fa_huo_dan_wei fhdw on cl.fhdwId=fhdw.id left join shou_huo_bu_men shbm on cl.shbmId=shbm.id where 1=1
and dd.ddh like CONCAT('%',#{ddh},'%')
and dd.ddztId=#{ddztId}
and ddzt.mc=#{ddztMc}
and cl.cph like CONCAT('%',#{cph},'%')
and yss.mc like CONCAT('%',#{yssMc},'%')
and wz.mc like CONCAT('%',#{wzMc},'%')
and fhdw.mc like CONCAT('%',#{fhdwMc},'%')
and shbm.mc like CONCAT('%',#{shbmMc},'%')
order by dd.bjsj desc limit #{rowNum},#{rows}

select dd.*,wzlx.mc wzlxMc,wz.mc wzMc,yss.mc yssMc,fhdw.mc fhdwMc,shbm.mc shbmMc from ding_dan dd left join wu_zi_lei_xing wzlx on dd.wzlxId=wzlx.id left join wu_zi wz on dd.wzId=wz.id
 left join yun_shu_shang yss on dd.yssId=yss.id left join fa_huo_dan_wei fhdw on dd.fhdwId=fhdw.id
 left join shou_huo_bu_men shbm on dd.shbmId=shbm.id where dd.id=#{id}

mapper.update:
update ding_dan set 


		ddh=#{ddh},


		ddztId=#{xddztId},


		yjzt=#{xyjzt},


		ejzt=#{xejzt},


		lxlx=#{lxlx},

		bjsj=now()

where ddztId=#{ddztId}

	and yjbfh=#{yjbfh}


	and ejbfh=#{ejbfh}


	and yjzt=#{yjzt}


	and ejzt=#{ejzt}

mapper.select:
select dd.* from ding_dan dd,ding_dan_zhuang_tai ddzt where dd.ddztId=ddzt.id and ddzt.mc=#{ddztMc}

 and dd.yjbfh=#{yjbfh}


 and dd.ejbfh=#{ejbfh}


 and dd.yjzt=#{yjzt}


 and dd.ejzt=#{ejzt}
